CREATE TABLE sf_crime_incidents_2014_01 (
    id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    incidnt_num BIGINT,
    category VARCHAR(30),
    descript VARCHAR(100),
    day_of_week VARCHAR(10),
    date DATETIME,
    time VARCHAR(5),
    pd_district VARCHAR(20),
    resolution VARCHAR(20),
    address VARCHAR(50),
    lon FLOAT,
    lat FLOAT,
    location VARCHAR(50)
    );

     
     
     
    
select date from sf_crime_incidents_2014_01

## SLICING ##
select date, 
	left(date,10) as left_dates,
    right (date, 8) as right_dates,
    substr(date, 6,2) as sliced_month
from sf_crime_incidents_2014_01;

## TRIM ##

select location,
	trim(LEADING'(' from location) as 'delete_('
    trim()
from sf_crime_incidents_2014_01;
#from the beginning ('leading'), the end ('trailing'), or both ('both', as used above).

## POSITION ##
#buat cek posisi ke brp kemunculan sebuah huruf
select category,address,
	position('A' in category ) as posisi,
    lower(category) as lowered_values, #berlkau jg untuk upper
    upper(address) as upper_values
from sf_crime_incidents_2014_01;


## CONCATENATE ##
select day_of_week , date,
	concat(day_of_week,', ', date) as day_date
from sf_crime_incidents_2014_01;

## Fill Null Value ##
#nul nya jadi no reseolution
select resolution, 
	coalesce(resolution, 'no resolution')
from sf_crime_incidents_2014_01



